import React,{useState} from "react";
import ExpensesFilter from "./ExpensesFilter";

import Card from "../UI/Card";
import ExpenseItem from "./ExpenseItem";
import './Expense.css';


export default function Expense(pro){
    const [oldyear,setyear]=useState("2020");
    const myfun=(data)=>{
          setyear(data);
    };
    
    return(
        <div>
            
        <Card className="expenses ">
        <ExpensesFilter sendvalue={oldyear} onDataPass={myfun}></ExpensesFilter>
        {pro.item.map((result)=>(
              <ExpenseItem title={result.tile}
              amount={result.amount}
              date={result}
              ></ExpenseItem>
        ))};
        <ExpenseItem title={pro.item[0].title} amount={pro.item[0].amount} date={pro.item[0].date}></ExpenseItem>
        <ExpenseItem title={pro.item[1].title} amount={pro.item[1].amount} date={pro.item[1].date}></ExpenseItem>
        <ExpenseItem title={pro.item[2].title} amount={pro.item[2].amount} date={pro.item[2].date}></ExpenseItem>
        <ExpenseItem title={pro.item[3].title} amount={pro.item[3].amount} date={pro.item[3].date}></ExpenseItem>
        </Card>
        </div>
    );
}
